import React from 'react';
//import SessionTitle from "./SessionTitle";
//import TimeRemaining from "./TimeRemaining";
//import Audio from "./Audio";
//import Paused from "./Paused";
//import ProgrssBar from "./ProgressBar";

function FocusInfo({
    focusOn,
    focusDuration,
    breakDuration,
    focusCount,
    breakCount,
    isTimerRunning,

}){


    function showFocusInfo(){
        const sessionInfo = document.querySelector("#sessionInfo");
        sessionInfo.classList.add("d-block");
        sessionInfo.classList.remove("d-none");
        const progressBar = document.querySelector(".progress");
        progressBar.classList.add("flex");
        progressBar.classList.remove("d-none");
    }

    if (isTimerRunning){
        showFocusInfo();
    }



    return (
        <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    )
}

export default FocusInfo;
