import React, { useEffect, useState } from "react";
import classNames from "../utils/class-names";
import { minutesToDuration, secondsToDuration } from "../utils/duration";
import useInterval from "../utils/useInterval";
import FocusButtons from "./FocusButtons"

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  //Set Focus Duration, still needs implementation of !<5 and !>60
  const [focusDuration,setFocusDuration] = useState(25);
  const [focusSeconds, setFocusSeconds] = useState(focusDuration *60)
  const [focusOn, setFocusOn] = useState(true);


  //Set Break Duration, !<1 !>15
  const [breakDuration, setBreakDuration] = useState(5)
  const [breakSeconds, setBreakSeconds] = useState(breakDuration*60)

  //Set Elapsed Time
  const [elapsedTime, setElapsedTime] = useState(0);



  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      console.log({elapsedTime})
      setElapsedTime(elapsedTime + 1);
      timerRun();

    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
    if (focusSeconds === focusDuration *60 && isTimerRunning===false){
      setFocusSeconds(focusDuration *60);
    }
    if (breakSeconds===breakDuration * 60 && isTimerRunning===false){
      setBreakSeconds(breakDuration *60)
    } 
    if(isTimerRunning===true){
      console.log("paused");
      disableTimerBtns();
    }else if(isTimerRunning===false){
      console.log("timer is running")
      disableTimerBtns();
      enableStopButton();
    }
  }

  const timerRun = ()=>{
    runFocusSession();
  }


  //ONSTOP
  const onStop = ()=>{
    console.log("stop")

  }

  function runFocusSession(){
    if(focusSeconds > 0){
      setFocusSeconds((count)=> count -1 )
    }else{
      setFocusOn(false);
      if (breakSeconds > 0){
        setBreakSeconds((count)=> count -1 )
      }else{
        console.log("Starting over");
        setFocusOn(true);
        setFocusSeconds(focusDuration *60 )
        setBreakSeconds(breakDuration * 60 )

      }
    }
    //Progress Bar
  }





  //Helper Functions

  function enableStopButton(){
    const stopBtn = document.querySelector("#stopBtn");
    stopBtn.classList.remove("disabled");
  }
  function disableStopButton(){
    const stopBtn = document.querySelector("#stopBtn");
    stopBtn.classList.add("disabled");
    stopBtn.getAttribute("disabled",true);
  }

  function enableTimerButns(){
    const timerBtns = document.querySelectorAll("#timerButton");
    for (let btn of timerBtns){
      btn.removeAttribute("disabled");
    }
  }
  function disableTimerBtns(){
    const timerBtns = document.querySelectorAll("#timerButton");
    for (let btn of timerBtns) {
      btn.setAttribute("disabled", true)
    }
  }

  //Handler
  useEffect(()=>{
    if(!isTimerRunning){
      setFocusSeconds(focusDuration *60 )
      setBreakSeconds(breakDuration *60 )
    }
  },[focusDuration,breakDuration])



  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <FocusButtons
            title="focus Duration"
            maxFocus = {60}
            minFocus = {5}
            diff = {5}
            value ={focusDuration}
            onChange = {setFocusDuration}
            disabled = {isTimerRunning}
            />
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {minutesToDuration(breakDuration)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick= {()=>{
                    console.log("decrease Break")
                    setBreakDuration(breakDuration-1)

                  }}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={()=>{
                    console.log("increase Break")
                    setBreakDuration(breakDuration+1)
                  }}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary disabled"
              id = "stopBtn"
              title="Stop the session"
              onClick = {()=>{
                onStop();
              }}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              Focusing for {secondsToDuration(focusDuration * 60)} minutes
              </h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration((focusDuration*60)-elapsedTime)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
